use mydb;

drop table if exists labs;
CREATE TABLE labs (
    id INT AUTO_INCREMENT PRIMARY KEY,
    category varchar(255),
    building varchar(255),
    area VARCHAR(255),
    room VARCHAR(255),
    name varchar(255),
    authority int,
    teacher varchar(255),
    contact varchar(255),
    description TEXT,
    summary text,
    image_url text
);

